DROP TABLE IF EXISTS `tb_designers`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `tb_designers` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '키',
  `name` varchar(20) DEFAULT NULL COMMENT '디자이너명',
  `description` varchar(1000) DEFAULT NULL COMMENT '디자이너소개',
  `profile` varchar(255) DEFAULT NULL COMMENT '프로필',
  `top_profile` varchar(255) DEFAULT NULL COMMENT '탑 디자이너 프로필',
  `top_yn` enum('Y','N') DEFAULT 'N' COMMENT 'TOP디자이너여부',
  `top_position` int DEFAULT NULL COMMENT '탑디자이너정렬',
  `show_yn` enum('Y','N') DEFAULT 'N' COMMENT '노출여부',
  `delete_yn` enum('Y','N') DEFAULT 'N' COMMENT '삭제여부',
  `create_by` varchar(12) DEFAULT NULL COMMENT '작성자',
  `create_date` datetime DEFAULT NULL COMMENT '작성일자',
  `update_by` varchar(12) DEFAULT NULL COMMENT '수정자',
  `update_date` datetime DEFAULT NULL COMMENT '수정일자',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=41 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='디자이너';
