INSERT INTO `tb_designers` VALUES (1,'황이슬','(현) 리슬 대표 (2014년 런칭) BTS 한복을 만든 디자이너','/resources/profile/designer/1/17.png','/resources/profile/designer/1/top/designer_thumb.jpg','Y',1,'Y','N','seerslab','2023-02-08 17:08:10',NULL,'2023-02-08 17:08:10'),(2,'하재경, 허인준','nuer는 두 명의 디자이너인 하재경과 허인준이 만든 모던 클래식 감성의 패션 브랜드','/resources/profile/designer/2/134.png','/resources/profile/designer/2/top/designer_thumb.jpg','Y',30,'Y','N','seerslab','2023-02-08 17:10:36',NULL,'2023-02-08 17:10:36'),(3,'이원재','51퍼센트(51PERCENT)는 기계공학을 전공한 디자이너','/resources/profile/designer/3/116.png','/resources/profile/designer/3/top/designer_thumb.jpg','Y',29,'Y','N','seerslab','2023-02-08 17:11:38',NULL,'2023-02-08 17:11:38'),(4,'Goyoungtae','‘패션에 예술을 불어넣다’라는 디자이너','/resources/profile/designer/4/153.png','/resources/profile/designer/4/top/designer_thumb.jpg','Y',28,'Y','N','seerslab','2023-02-08 17:12:24',NULL,'2023-02-08 17:12:24'),(5,'심윤주, 심보한','핸드백 디자이너. 패션에 뜻을 두고, 이태리에서 패션디자인을 전공','/resources/profile/designer/5/162.png','/resources/profile/designer/5/top/designer_thumb.jpg','Y',27,'Y','N','seerslab','2023-02-08 17:13:02',NULL,'2023-02-08 17:13:02'),(6,'이두라','정교한 스티치 라인과 광택감이 돋보이는 디자인을 주로하는 디자이너','/resources/profile/designer/6/designer_thumb_001.png','/resources/profile/designer/6/top/designer_thumb.png','Y',26,'Y','N','seerslab','2023-02-08 17:13:56',NULL,'2023-02-08 17:13:56'),(7,'이영은','패션의류학 졸업 후 10년간의 다양한 패션회사에서 일해오다가 회사 설립.','/resources/profile/designer/7/192.png','/resources/profile/designer/7/top/designer_thumb.jpg','Y',25,'Y','N','seerslab','2023-02-08 17:14:33',NULL,'2023-02-08 17:14:33'),(8,'신한나','신한나(HANNAH SHIN) 건축적인 실루엣을 기반으로하는 디자이너','/resources/profile/designer/8/205.png','/resources/profile/designer/8/top/designer_thumb.jpg','Y',23,'Y','N','seerslab','2023-02-08 17:15:04','seerslab','2023-02-08 17:23:55'),(9,'송현희, 최경호','남성복 출신의 최경호 디자이너와 여성복 출신의 송현희 디자이너','/resources/profile/designer/9/218.png','/resources/profile/designer/9/top/designer_thumb.jpg','Y',24,'Y','N','seerslab','2023-02-08 17:15:32',NULL,'2023-02-08 17:15:32'),(10,'최중훈','색감이 뛰어난 패션디자이너 프랑스에서 패션디자이너1','/resources/profile/designer/10/142.png','/resources/profile/designer/10/top/designer_thumb.jpg','Y',22,'Y','N','seerslab','2023-02-08 18:03:44',NULL,'2023-02-20 10:19:18'),(11,'123d','123','/resources/profile/designer/11/176.png','/resources/profile/designer/11/top/177.png','Y',NULL,'Y','Y','seerslab','2023-02-08 18:31:50',NULL,'2023-02-08 18:32:09'),(12,'12345','123','/resources/profile/designer/12/악어_호에에에.png',NULL,'N',NULL,'N','Y','seerslab','2023-02-08 18:42:11',NULL,'2023-02-08 18:43:26'),(13,'123','123','/resources/profile/designer/13/fromwhere.png','/resources/profile/designer/13/top/fromwhere.png','Y',NULL,'Y','Y','duckduc1','2023-02-17 09:57:51',NULL,'2023-02-17 09:57:51'),(14,'디디자자','디자소개디자소개','/resources/profile/designer/14/details_thumb_003.png',NULL,'N',NULL,'Y','Y','duckduc1','2023-02-20 11:00:24',NULL,'2023-02-20 13:56:41'),(15,'123','123','/resources/profile/designer/15/details_thumb_003.png',NULL,'N',NULL,'N','Y','duckduc1','2023-02-20 16:00:51',NULL,'2023-02-20 16:00:51'),(16,'123','1231234124','/resources/profile/designer/16/details_thumb_003.png',NULL,'N',NULL,'Y','Y','duckduc1','2023-02-20 16:01:01',NULL,'2023-02-20 16:04:45'),(17,'123','123','',NULL,'N',NULL,'Y','Y','duckduc1','2023-02-20 16:02:05','duckduc1','2023-02-20 16:02:05'),(18,'123','123','',NULL,'N',NULL,'N','Y','duckduc1','2023-02-20 16:22:30','duckduc1','2023-02-20 16:22:30'),(19,'123','123456','',NULL,'N',NULL,'Y','Y','duckduc1','2023-02-20 16:23:27','duckduc1','2023-02-20 16:23:27'),(20,'1111','2222','',NULL,'N',21,'Y','N','duckduc1','2023-02-20 16:24:45','duckduc1','2023-02-20 16:24:45'),(21,'555','57777','',NULL,'N',20,'Y','N','duckduc1','2023-02-20 16:26:01','duckduc1','2023-02-20 16:26:01'),(22,'666','8888','',NULL,'N',19,'Y','N','duckduc1','2023-02-20 16:29:19','duckduc1','2023-02-20 16:29:19'),(23,'sdfsdfsdf','sdfsdfsfsdfsd','/resources/profile/designer/23/다운로드.jpeg',NULL,'N',18,'N','N',