DROP TABLE IF EXISTS `tb_popups`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `tb_popups` (
  `id` bigint NOT NULL AUTO_INCREMENT,
  `title` varchar(100) DEFAULT NULL,
  `image` varchar(100) DEFAULT NULL COMMENT '팝업이미지',
  `show_from` datetime DEFAULT NULL COMMENT '노출시작',
  `show_to` datetime DEFAULT NULL COMMENT '노출종료',
  `show_yn` enum('Y','N') DEFAULT 'N' COMMENT '노출여부',
  `delete_yn` int DEFAULT NULL COMMENT '삭제여부',
  `create_by` varchar(12) DEFAULT NULL COMMENT '작성자',
  `create_date` datetime DEFAULT NULL,
  `update_by` varchar(12) DEFAULT NULL COMMENT '수정자',
  `update_date` datetime DEFAULT NULL COMMENT '수정일자',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='팝업';
