DROP TABLE IF EXISTS `tb_users`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `tb_users` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '키',
  `email` varchar(200) DEFAULT NULL COMMENT '이메일',
  `password` varchar(100) DEFAULT NULL COMMENT '비밀번호',
  `nickname` varchar(200) DEFAULT NULL COMMENT '닉네임',
  `gender` enum('MALE','FEMALE') DEFAULT NULL COMMENT '성별',
  `profile` varchar(500) DEFAULT NULL COMMENT '프로필',
  `device_id` varchar(200) DEFAULT NULL COMMENT '디바이스id',
  `channel_id` varchar(200) DEFAULT NULL COMMENT '구글/애플 로그인시 받는 payload 정보',
  `provider` varchar(10) DEFAULT NULL COMMENT '로그인서비스제공자',
  `platform` varchar(10) DEFAULT NULL COMMENT '플랫폼',
  `last_login` datetime DEFAULT NULL COMMENT '최종로그인',
  `last_logout` datetime DEFAULT NULL COMMENT '최종로그아웃',
  `policy_use_read` enum('Y','N') DEFAULT 'N' COMMENT '이용약관 읽기 여부',
  `policy_privacy_read` enum('Y','N') DEFAULT 'N' COMMENT '개인정보 처리방침 읽기 여부',
  `leave_yn` enum('Y','N') DEFAULT 'N' COMMENT '탈퇴여부',
  `leave_type` enum('SELF','ADMIN') DEFAULT NULL COMMENT '탈퇴구분',
  `create_date` datetime DEFAULT NULL COMMENT '가입일자',
  `update_date` datetime DEFAULT NULL COMMENT '수정일자',
  `leave_date` datetime DEFAULT NULL COMMENT '탈퇴일자',
  `leave_by` varchar(12) DEFAULT NULL COMMENT '탈퇴처리자',
  `update_by` varchar(12) DEFAULT NULL COMMENT '수정처리자',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=64 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='사용자';
/*!40101 SET character_set_client = @saved_cs_client */;

